import passport from "passport";
import { Strategy as LocalStrategy } from "passport-local";
import { Strategy as GoogleStrategy } from "passport-google-oauth20";
import { Express } from "express";
import session from "express-session";
import { scrypt, randomBytes, timingSafeEqual } from "crypto";
import { promisify } from "util";
import { storage } from "./storage";
import { User as SelectUser } from "@shared/schema";

declare global {
  namespace Express {
    interface User extends SelectUser {}
  }
}

const scryptAsync = promisify(scrypt);

async function hashPassword(password: string) {
  const salt = randomBytes(16).toString("hex");
  const buf = (await scryptAsync(password, salt, 64)) as Buffer;
  return `${buf.toString("hex")}.${salt}`;
}

async function comparePasswords(supplied: string, stored: string) {
  const [hashed, salt] = stored.split(".");
  const hashedBuf = Buffer.from(hashed, "hex");
  const suppliedBuf = (await scryptAsync(supplied, salt, 64)) as Buffer;
  return timingSafeEqual(hashedBuf, suppliedBuf);
}

export function setupAuth(app: Express) {
  const sessionSettings: session.SessionOptions = {
    secret: process.env.SESSION_SECRET || 'sendwise-secret-key-development-only',
    resave: false,
    saveUninitialized: true,
    store: storage.sessionStore,
    cookie: {
      secure: false, // Set to false for development
      httpOnly: true,
      maxAge: 24 * 60 * 60 * 1000, // 24 hours
      sameSite: 'lax'
    }
  };

  app.set("trust proxy", 1);
  app.use(session(sessionSettings));
  app.use(passport.initialize());
  app.use(passport.session());

  // Local Strategy (Basic Auth)
  passport.use(
    new LocalStrategy(
      { usernameField: 'email' },
      async (email, password, done) => {
        try {
          const user = await storage.getUserByEmail(email);
          if (!user || user.authProvider !== 'basic' || !user.password) {
            return done(null, false, { message: 'Invalid credentials' });
          }

          const isValidPassword = await comparePasswords(password, user.password);
          if (!isValidPassword) {
            return done(null, false, { message: 'Invalid credentials' });
          }

          return done(null, user);
        } catch (error) {
          return done(error);
        }
      }
    )
  );

  // Google OAuth Strategy
  if (process.env.GOOGLE_CLIENT_ID && process.env.GOOGLE_CLIENT_SECRET) {
    passport.use(
      new GoogleStrategy(
        {
          clientID: process.env.GOOGLE_CLIENT_ID,
          clientSecret: process.env.GOOGLE_CLIENT_SECRET,
          callbackURL: process.env.NODE_ENV === 'production' 
            ? `${process.env.REPLIT_DEV_DOMAIN}/api/auth/google/callback`
            : process.env.REPLIT_DEV_DOMAIN 
              ? `https://${process.env.REPLIT_DEV_DOMAIN}/api/auth/google/callback`
              : "/api/auth/google/callback"
        },
        async (accessToken, refreshToken, profile, done) => {
          try {
            const email = profile.emails?.[0]?.value;
            if (!email) {
              return done(new Error('No email provided by Google'));
            }

            // Check if user already exists
            let user = await storage.getUserByEmail(email);

            if (user) {
              // Update Google ID if not set
              if (!user.googleId && user.authProvider === 'basic') {
                user = await storage.updateUser(user.id, {
                  googleId: profile.id,
                  authProvider: 'google',
                  profileImageUrl: profile.photos?.[0]?.value,
                  emailVerified: true
                });
              }
            } else {
              // Create new user
              user = await storage.createUser({
                email,
                googleId: profile.id,
                firstName: profile.name?.givenName,
                lastName: profile.name?.familyName,
                profileImageUrl: profile.photos?.[0]?.value,
                authProvider: 'google',
                emailVerified: true,
                isActive: true
              });
            }

            return done(null, user);
          } catch (error) {
            return done(error);
          }
        }
      )
    );
  }

  passport.serializeUser((user, done) => done(null, user.id));
  passport.deserializeUser(async (id: string, done) => {
    try {
      const user = await storage.getUser(id);
      done(null, user);
    } catch (error) {
      done(error);
    }
  });

  // Registration endpoint
  app.post("/api/register", async (req, res, next) => {
    try {
      const { email, password, username, firstName, lastName } = req.body;

      if (!email || !password) {
        return res.status(400).json({ message: "Email and password are required" });
      }

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(email);
      if (existingUser) {
        return res.status(400).json({ message: "User already exists with this email" });
      }

      // Check username if provided
      if (username) {
        const existingUsername = await storage.getUserByUsername(username);
        if (existingUsername) {
          return res.status(400).json({ message: "Username already taken" });
        }
      }

      const hashedPassword = await hashPassword(password);
      const user = await storage.createUser({
        email,
        username,
        password: hashedPassword,
        firstName,
        lastName,
        authProvider: 'basic',
        isActive: true
      });

      // Remove password from response
      const { password: _, ...userResponse } = user;

      req.login(user, (err) => {
        if (err) return next(err);
        res.status(201).json(userResponse);
      });
    } catch (error) {
      console.error('Registration error:', error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Login endpoint
  app.post("/api/login", (req, res, next) => {
    passport.authenticate("local", (err: any, user: any, info: any) => {
      if (err) {
        return res.status(500).json({ message: "Internal server error" });
      }
      if (!user) {
        return res.status(401).json({ message: info?.message || "Invalid credentials" });
      }

      req.login(user, (err) => {
        if (err) {
          return res.status(500).json({ message: "Login failed" });
        }

        // Remove password from response
        const { password, ...userResponse } = user;
        res.json(userResponse);
      });
    })(req, res, next);
  });

  // Google OAuth routes
  app.get("/api/auth/google", 
    passport.authenticate("google", { scope: ["profile", "email"] })
  );

  app.get("/api/auth/google/callback",
    passport.authenticate("google", { failureRedirect: "/auth?error=google_auth_failed" }),
    (req, res) => {
      // Successful authentication, redirect to dashboard
      res.redirect("/");
    }
  );

  // Logout endpoint
  app.post("/api/logout", (req, res, next) => {
    req.logout((err) => {
      if (err) return next(err);
      res.sendStatus(200);
    });
  });

  // Get current user (legacy endpoint - redirect to new endpoint)
  app.get("/api/user", (req, res) => {
    if (!req.isAuthenticated() || !req.user) {
      return res.sendStatus(401);
    }

    // Remove password from response
    const { password, ...userResponse } = req.user;
    res.json(userResponse);
  });
}

client/src/components/purchase-form.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";

const purchaseSchema = z.object({
  actualQuantity: z.string().min(1, "Quantity is required"),
  actualUnitPrice: z.string().min(1, "Unit price is required"),
  actualAmount: z.string().min(1, "Total amount is required"),
  notes: z.string().optional(),
});

type PurchaseFormData = z.infer<typeof purchaseSchema>;

interface PurchaseFormProps {
  item: any;
  onSubmit: (data: PurchaseFormData) => void;
  onCancel: () => void;
  isLoading: boolean;
}

export default function PurchaseForm({ item, onSubmit, onCancel, isLoading }: PurchaseFormProps) {
  const form = useForm<PurchaseFormData>({
    resolver: zodResolver(purchaseSchema),
    defaultValues: {
      actualQuantity: item?.plannedQuantity?.toString() || "1",
      actualUnitPrice: item?.plannedUnitPrice?.toString() || "0",
      actualAmount: item?.plannedAmount?.toString() || "0",
      notes: "",
    },
  });

  const calculateTotal = () => {
    const quantity = parseFloat(form.getValues("actualQuantity") || "0");
    const unitPrice = parseFloat(form.getValues("actualUnitPrice") || "0");
    const total = quantity * unitPrice;
    form.setValue("actualAmount", total.toFixed(2));
  };

  if (!item) return null;

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
        {/* Item Info */}
        <Card>
          <CardContent className="p-4">
            <div className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-500">Item:</span>
                <span className="font-medium">{item.name}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-500">Planned:</span>
                <span>
                  {item?.plannedQuantity} {item?.unit} Ã— ${parseFloat(item?.plannedUnitPrice || "0").toFixed(2)} = ${parseFloat(item?.plannedAmount || "0").toFixed(2)}
                </span>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Purchase Details */}
        <div className="grid grid-cols-2 gap-4">
          <FormField
            control={form.control}
            name="actualQuantity"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Actual Quantity</FormLabel>
                <FormControl>
                  <Input 
                    type="number" 
                    step="0.1" 
                    placeholder="2.5" 
                    {...field}
                    onChange={(e) => {
                      field.onChange(e);
                      calculateTotal();
                    }}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="actualUnitPrice"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Actual Unit Price</FormLabel>
                <FormControl>
                  <Input 
                    type="number" 
                    step="0.01" 
                    placeholder="5.99" 
                    {...field}
                    onChange={(e) => {
                      field.onChange(e);
                      calculateTotal();
                    }}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        <FormField
          control={form.control}
          name="actualAmount"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Total Amount Paid</FormLabel>
              <FormControl>
                <Input 
                  type="number" 
                  step="0.01" 
                  placeholder="14.98" 
                  {...field} 
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="notes"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Notes (Optional)</FormLabel>
              <FormControl>
                <Input 
                  placeholder="Store, special offers, etc..." 
                  {...field} 
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Variance Display */}
        {(() => {
          const plannedAmount = parseFloat(item?.plannedAmount || "0");
          const actualAmount = parseFloat(form.watch("actualAmount") || "0");
          const variance = actualAmount - plannedAmount;

          if (Math.abs(variance) > 0.01) {
            return (
              <Card className={variance > 0 ? "border-red-200 bg-red-50" : "border-green-200 bg-green-50"}>
                <CardContent className="p-4">
                  <div className="text-sm">
                    <span className={variance > 0 ? "text-red-700" : "text-green-700"}>
                      {variance > 0 ? "Over budget" : "Under budget"} by ${Math.abs(variance).toFixed(2)}
                    </span>
                  </div>
                </CardContent>
              </Card>
            );
          }
          return null;
        })()}

        <div className="flex gap-2 pt-4">
          <Button
            type="button"
            variant="outline"
            onClick={onCancel}
            className="flex-1"
          >
            Cancel
          </Button>
          <Button
            type="submit"
            disabled={isLoading}
            className="flex-1"
          >
            {isLoading ? "Recording..." : "Record Purchase"}
          </Button>
        </div>
      </form>
    </Form>
  );
}

